<?phpnamespace App\Http\Controllers;use App\Helpers\ApiException;use App\Helpers\ResponseHandler;use App\Http\Requests\InvoiceRequest;use App\Http\Resources\InvoiceResource;use App\Models\Invoice;use App\Models\User;use Illuminate\Http\Request;class InvoiceController extends Controller{    /**     * @throws \Throwable     */    public function index(Request $request)    {       /** @var User $user */        $user = auth()->user();        if (!$user->can('viewAnyInvoice')) {            throw new ApiException('You do not have permission to view any invoice', [],403);        }        $invoices = Invoice::query()            ->when($request->customer_name, function ($query, $customerName) {                $query->whereHas('customer', function ($q) use ($customerName) {                    $q->where('name', 'like', "%$customerName%");                });            })            ->when($request->invoice_date, function ($query, $invoiceDate) {                $query->where('invoice_date', $invoiceDate);            })            ->when($request->amount, function ($query, $amount) {                $query->where('amount', $amount);            })            ->when($request->status, function ($query, $status) {                $query->where('status', $status);            })->paginate($request->per_page ?? 10);        return ResponseHandler::success('All Invoices', InvoiceResource::collection($invoices));    }    public function store(InvoiceRequest $request)    {        /** @var User $user */        $user = auth()->user();        if (!$user->can('createInvoice')) {            throw new ApiException('You do not have permission to create any invoice', [],403);        }        $invoice = Invoice::create($request->validated());        return new InvoiceResource($invoice);    }    public function show(Invoice $invoice)    {        return new InvoiceResource($invoice);    }    public function update(InvoiceRequest $request, Invoice $invoice)    {        /** @var User $user */        $user = auth()->user();        if (!$user->can('updateInvoice')) {            throw new ApiException('You do not have permission to update any invoice', [],403);        }        $invoice->update($request->validated());        return ResponseHandler::success('Updated Successfully', new InvoiceResource($invoice));    }    public function destroy(Invoice $invoice)    {        /** @var User $user */        $user = auth()->user();        if (!$user->can('deleteInvoice')) {            throw new ApiException('You do not have permission to delete any invoice', [],403);        }        $invoice->delete();        return response()->noContent();    }}